mapper.select:

	SELECT *
	FROM(
		SELECT artist_Id, alb_name, release_at, picture, id, RANK() OVER (ORDER BY ID DESC) RANK
		FROM ALBUM
		WHERE ARTIST_ID = ${artistId}
		)
	WHERE RANK > (${cri.pageNum} -1) * ${cri.amount}
    AND RANK <= ${cri.pageNum} * ${cri.amount}

SELECT COUNT(*)
FROM ALBUM
WHERE ARTIST_ID = #{artistId}

SELECT COUNT(*)
FROM
PURCHASED_MUSIC
WHERE MEMBER_ID = #{id}
AND MUSIC_ID = #{musicId}

SELECT COUNT(ID)
FROM ALBUM
WHERE ARTIST_ID = #{id}

mapper.update:
UPDATE ARTIST
image = #{image}, gender = #{gender}, genre = #{genre}, content = #{content}, type = #{type}, name = #{name}
WHERE MEMBER_ID = #{memberId}
