mapper.select:
SELECT * FROM (SELECT RANK() OVER(ORDER BY CREATED_AT DESC) RANK, MEMBER_ID, IDENTIFY, ARTWORK, STATUS, FILE_GROUP_ID, CREATED_AT, UPDATED_AT FROM PROMOTION_REQUEST
WHERE MEMBER_ID LIKE '%'||#{keyword}||'%'
) WHERE RANK > (#{pageNum} -1) * #{amount} AND RANK <= #{pageNum} * #{amount}

mapper.update:

{ call judge_artist(
  #{v_member_id, mode=IN, jdbcType=VARCHAR, javaType=string},
  #{v_status, mode=IN, jdbcType=VARCHAR, javaType=string},
  #{v_updated_at, mode=IN, jdbcType=DATE, javaType=date}
  )}

UPDATE PROMOTION_REQUEST
MEMBER_ID = #{memberId}, IDENTIFY = #{identify}, ARTWORK = #{artwork}, STATUS = #{status}, FILE_GROUP_ID = #{fileGroupId}, created_at = #{createAt}
WHERE MEMBER_ID = #{memberId}
