mapper.select:
select id, charge_at, mileage, name from (select rank() OVER (order by a.charge_at desc) rank, a.id, a.charge_at, a.mileage, b.name from charge_history a, common_code b where a.type = b.id and a.member_id = #{memberId}
and a.charge_at BETWEEN TO_DATE(#{startDate},'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')+0.5
and a.charge_at BETWEEN TO_DATE('2022-01-01','YYYY-MM-DD') AND TO_DATE('2022-12-31', 'YYYY-MM-DD')+0.5
order by id desc ) where rank > (#{cri.pageNum}-1) * #{cri.amount} and rank<= #{cri.pageNum} * #{cri.amount}

select count(id) from charge_history where member_id = #{memberId}
and charge_at BETWEEN TO_DATE(#{startDate},'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')+0.5
and charge_at BETWEEN TO_DATE('2022-01-01','YYYY-MM-DD') AND TO_DATE('2022-12-31', 'YYYY-MM-DD')+0.5

select sum(mileage) from charge_history where member_id = #{memberId}
and charge_at BETWEEN TO_DATE(#{startDate},'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')+0.5
and charge_at BETWEEN TO_DATE('2022-01-01','YYYY-MM-DD') AND TO_DATE('2022-12-31', 'YYYY-MM-DD')+0.5
