mapper.update:
update post

  title = #{title}
  content = #{content}

where id = #{id}

mapper.select:
select *
from post


  AND member_id = #{memberId}
  AND writer = #{writer}
  AND title = #{title}



    order by hit asc
    order by created_at asc


    order by hit desc
    order by created_at desc


    order by created_at desc
